mapper.update:
update sys_config
set key = #{key,jdbcType=VARCHAR},
  value = #{value,jdbcType=VARCHAR},
  is_delete = #{isDelete,jdbcType=TINYINT},
  created_at = #{createdAt,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, key, value, is_delete, created_at
from sys_config
where id = #{id,jdbcType=INTEGER}
